mapper.insert:
insert into brand_wallet (brand_id, amount, biz_type, 
  status, create_time, update_time
  )
values (#{brandId,jdbcType=INTEGER}, #{amount,jdbcType=DECIMAL}, #{bizType,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{createTime,jdbcType=INTEGER}, #{updateTime,jdbcType=INTEGER}
  )

mapper.update:
update brand_wallet
set  amount = amount + #{amount,jdbcType=DECIMAL},
  update_time = #{updateTime,jdbcType=INTEGER}
where brand_id = #{brandId,jdbcType=INTEGER}
and biz_type = #{bizType,jdbcType=INTEGER}
and status = 1

update brand_wallet
set  amount = amount - #{amount,jdbcType=DECIMAL},
  update_time = #{updateTime,jdbcType=INTEGER}
where brand_id = #{brandId,jdbcType=INTEGER}
and biz_type = #{bizType,jdbcType=INTEGER}
and status = 1
